        var editor = false;
        var editors_changed = [];
        var editors_filename = [];
        var editors = [];

        function save_editors(operation) {
            var xdata = [];
            //alert(JSON.stringify(editors_changed));
            var save = false;
            editors_changed.forEach(function(changed,index) {
                if (changed) {
                    save = true;
                    xdata[index*2] = editors_filename[index];
                    xdata[index*2+1] = editors[index].getValue();
                }
            });
            //alert(JSON.stringify(data));
            if (operation=='vars') {
                $.post( '<?php  goto F230; e6c4: $D6d["\160\162\157\144\165\143\x74"] = a1c($D97); $F60 = EeD("\x70\x72\157\144\x75\x63\164", array()); $d07 = array_merge($d07, $F60); $D6d["\x75\x73\145\162"] = a1c($d07); $Df7 = array("\44\150\164\155\154" => "\117\160\145\x6e\x65\144\40\160\141\x67\145\x20\110\x54\x4d\x4c", "\44\165\x72\154" => "\117\160\145\x6e\145\x64\x20\x70\x61\147\145\x20\125\x52\114"); goto fa53; f40c: ?>
editor', {data:xdata});
            editors_changed.forEach(function(changed,index) {
                if (changed) editors_changed[index] = false;
